import React from "react";

class Sohail extends React.Component{
  constructor(props){
    super(props)
    this.state = {
      name : " ",
      todo : []
    }
  }

  onsubmits = (e) =>{

    e.preventDefault();

    let todo = this.state.todo;
    let input = this.state.name;
    todo.push(input)

    this.setState({

        todo : todo,
        name : " "

    })

  }  

  handle = (event) => {
    // console.log(event.target.value)

    this.setState({
      name : event.target.value
    })

    }

    delete = (event) => {
      let id = event.target.id;
      // console.log(id)

      var item = this.state.todo;
      item.splice(id,1);
      this.setState({
        todo: item,
        name : " "
      })
    }


  render(){
                  // Another Method 

              // let itemlist = this.state.todo.map((e,i)=>
              // <li  key={i}>{e} <span onClick={this.delete}id={i}>X</span></li>
              // )

    return(

      <React.Fragment>
        <div className="div1">
        <form onSubmit={this.onsubmits}>
        <input type="text" className="input" value={this.state.name} onChange={this.handle} />
        <button>Press</button>
        </form>

        <br />
        </div>
   <div>

           {
             this.state.todo.map((e,i) =>{
               return(
               <li className="c" key={i}>{e} <span onClick={this.delete}id={i}>X</span></li>
               )
             }

             )
           }

  </div>

      </React.Fragment>
    )
  }
}
export default Sohail;
